                </View>
                <View style={styles.ia_group}>
                    <Text style={styles.ia_title}>출생정보</Text>
                    <View style={{ flex: 1, }}>
                        <TouchableOpacity
                            style={styles.ia_btn_modal}
                            onPress={() => {
