                            }}>
                            {_selectBornTimeType_BtnText()}
                        </TouchableOpacity>
                        {_selectTime_InputArea()}
                    </View>
                </View>
                <View style={styles.ia_group}>
                    <Text style={styles.ia_title}>직업</Text>
