import Modal from "react-native-modal";
import {
    Text,
    View,
    StyleSheet,
    TouchableOpacity,
} from 'react-native';
import React from "react";

const DefaultModalPopup = ({ text, isVisible, onPressConfirm }) => {
    return (
        <Modal
            isVisible={isVisible}
            useNativeDriver={true}
            hideModalContentWhileAnimating={true}
            style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
            <View style={modalStyles.md_container}>
                <View style={modalStyles.md_wrapper}>
                    <View style={modalStyles.md_message_wrap}>
                        <Text style={modalStyles.md_message}>{text}</Text>
                        <View style={modalStyles.mdm_btn_wrap}>
                            <TouchableOpacity
                                style={modalStyles.mdm_btn}
                                onPress={onPressConfirm}>
                                <Text style={modalStyles.mdm_btn_text}>확인</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </View>
            </View>
        </Modal>
    )
}

const SelectModalPopup = ({ items, isVisible, onPressItem }) => {
    return (
        <Modal
        isVisible={isVisible}
            useNativeDriver={true}
            hideModalContentWhileAnimating={true}
            style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
            <View style={modalStyles.md_container}>
                <View style={modalStyles.md_wrapper}>
                    <Text style={modalStyles.md_title}>입력 유형 선택</Text>
                    <View style={modalStyles.md_btn_group}>
                        {/* items.map */}
                        <TouchableOpacity
                            style={modalStyles.md_btn}
                            onPress={() => {
                                onPressItem(0)
                            }}>
                            <Text style={modalStyles.md_btn_text}>서기로 입력</Text>
                        </TouchableOpacity>
                        <TouchableOpacity
                            style={modalStyles.md_btn}
                            onPress={() => {
                                onPressItem(1)
                            }}>
                            <Text style={modalStyles.md_btn_text}>간지 입력</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        </Modal>
    )
}

const modalStyles = StyleSheet.create({
    md_container: {
        width: 320,
        backgroundColor: '#fff',
        borderRadius: 10,
    },
    md_wrapper: {
        // padding: 10,
    },
    md_title: {
        alignItems: 'center',
        fontSize: 18,
        fontWeight: '700',
    },
    md_message_wrap: {
        alignItems: 'center',
    },
    md_message: {
        marginTop: 30,
        marginBottom: 30,
        fontSize: 16,
        fontWeight: '500',
    },
    mdm_btn_wrap: {
        flexDirection: 'row',
    },
    mdm_btn: {
        flex: 1,
        borderTopWidth: 1,
        alignItems: 'center',
    },
    mdm_btn_text: {
        color: '#000',
        fontSize: 14,
        paddingTop: 10,
        paddingBottom: 10,
    },
    md_btn_group: {
        marginTop: 10,
        marginLeft: 10,
    },
    md_btn: {
        paddingTop: 10,
        paddingBottom: 10,
    },
    md_btn_text: {
        fontWeight: '500',
        fontSize: 14,
    }
});

export default {DefaultModalPopup, SelectModalPopup};
